import { Player } from "./Player";
import { Position } from "../enums/Position";

export class DragAndDropPlayer extends Player {
    source:string;

    constructor (player:Player) {
        super(player.getName(), player.getPlayerType(), player.getCurrentPosition());
        this.setNumber(player.getNumber());
        this.setPlayerLink(player.getPlayerLink());
        this.setOnCourt(player.isOnCourt());
        this.setServing(player.isServing());
    }

    public getSource():string {
        return this.source;
    }

    public setSource(source:string): void
    {
        this.source = source;
    }

    public toString(): string {
        return JSON.stringify(this);
    }

    public static revive(playerToRevive:DragAndDropPlayer):Player {

        return this.reviveRecursive(playerToRevive, false);
    }

    private static reviveRecursive(playerToRevive:Player, isPlayerLink:boolean):Player
    {
        let revivedPosition = Position.revive(playerToRevive.playerType);
        let revivedPlayer = new DragAndDropPlayer(playerToRevive);
        revivedPlayer.setOnCourt(playerToRevive.onCourt);

        revivedPlayer.setNumber(playerToRevive.num);
        revivedPlayer.setServing(playerToRevive.serving);
        if (!isPlayerLink && playerToRevive.playerLink !== undefined) {
            revivedPlayer.setPlayerLink(Player.reviveRecursive(playerToRevive.playerLink, true));
        }
        return revivedPlayer;
    }

    public asPlayer()
}
